mapper.update:

UPDATE
    seckill
SET
    number = number - 1;
WHERE seckill_id = #{seckillId}
AND start_time  <=  #{killTIme}
AND end_time >= #{killTIme}
AND number > 0;

mapper.select:

SELECT seckill_id, name, number, create_time, start_time, end_time
FROM seckill
WHERE seckill_id = #{seckillId}
